import React from "react";
import ReactDOM from "react-dom";

const Header = (props) => {
  console.log(props);
  return (
    <>
      <h1>{props.name}</h1>
    </>
  );
};

const Content = (props) => {
  let arr = props.parts;
  // console.log(arr);
  // console.log(arr[0]);

  return (
    <div>
      {arr.map((elem) => {
        // console.log(elem);
        return (
          <p key={elem.name}>
            {elem.name} {elem.exercises}
          </p>
        );
      })}
    </div>
  );
};

const Total = (props) => {
  let total = props.parts.reduce((all, item) => item.exercises + all, 0);

  return (
    <div>
      <p>
        <b>total of {total} exercises</b>
      </p>
    </div>
  );
};

const Course = (props) => {
  // console.log(props.course);
  let arrCourse = props.course;

  return arrCourse.map((item, index) => {
    console.log(item);
    console.log(item.name);
    // console.log(index);
    // <Header name={item.name} />;
    return (
      <Header name={item.name} />
    )
  }); 

  // return <Header name={arrCourse[0].name} />;
};

const App = () => {
  // const course = {
  //   name: "Half Stack application development",
  //   parts: [
  //     {
  //       name: "Fundamentals of React",
  //       exercises: 10
  //     },
  //     {
  //       name: "Using props to pass data",
  //       exercises: 7
  //     },
  //     {
  //       name: "State of a component",
  //       exercises: 14
  //     },
  //     {
  //       name: "Redux",
  //       exercises: 11
  //     }
  //   ]
  // };

  const course = [
    {
      name: "Half Stack application development",
      id: 1,
      parts: [
        {
          name: "Fundamentals of React",
          exercises: 10,
          id: 1,
        },
        {
          name: "Using props to pass data",
          exercises: 7,
          id: 2,
        },
        {
          name: "State of a component",
          exercises: 14,
          id: 3,
        },
        {
          name: "Redux",
          exercises: 11,
          id: 4,
        },
      ],
    },
    {
      name: "Node.js",
      id: 2,
      parts: [
        {
          name: "Routing",
          exercises: 3,
          id: 1,
        },
        {
          name: "Middlewares",
          exercises: 7,
          id: 2,
        },
      ],
    },
  ];

  return (
    <>
      <h1>Web development curriculum</h1>
      <Course course={course} />
    </>
  );
};

ReactDOM.render(<App />, document.getElementById("root"));
