import React,  {useState, useEffect} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import axios from 'axios';

const App = () => {

  const [country, setCountry] = useState("");
  const [countryInfo, setCountryInfo] = useState([]);


  const handleChange = (elem) => {
    setCountry(elem.target.value);
    console.log(countryInfo);
  }

  let countryVal = `https://restcountries.eu/rest/v2/name/${country}`;

  if (country !== "") {
    axios.get(countryVal).then((response) => {
      // console.log(response.data);
      setCountryInfo(response.data);
    });    
  }

  return (
    <div className="container">
      <p>find countries</p>
      <input type="text" placeholder="seach a country" onChange={handleChange} />

      <div>
        <p>Placement for countries</p>
      </div>
    </div>
  );
}

ReactDOM.render(<App />, document.getElementById("root"));
