    })

  return (
    <div className="container">
      <p>find countries</p>
      <input
        type="text"
        placeholder="search a country"
        onChange={handleChange}
      />

      <p>{message}</p>

      <div>
        {console.log(countryInfo)}
      </div>

      {/* {countryInfo} */}
    </div>
  );
}

ReactDOM.render(<App />, document.getElementById("root"));
