import React,  {useState, useEffect} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import axios from 'axios';

const App = () => {

  const [country, setCountry] = useState("");
  const [countryInfo, setCountryInfo] = useState([]);


  const handleChange = (elem) => {
    setCountry(elem.target.value);
    console.log(elem.target.value);
    console.log(countryInfo);
  }

  let countryVal = `https://restcountries.eu/rest/v2/name/${country}`;

  axios.get(countryVal).then((response) => {
    setCountryInfo(response.data);
    const renderInfo = () => {
      if(countryInfo.length == 0) {
        return <p>No country yet</p>;
      }
    }
  }).catch((error) => {
    console.log(error);
  });


  return (
    <div className="container">
      <p>find countries</p>
      <input type="text" placeholder="seach a country" onChange={handleChange} />

     <div>
      {renderInfo()}
     </div>
    </div>
  );
}

ReactDOM.render(<App />, document.getElementById("root"));
