import React,  {useState} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import axios from 'axios';

const App = () => {
  const [message, setMessage] = useState("No country searched");
  const [country, setCountry] = useState("");
  const [countryInfo, setCountryInfo] = useState([]);

  const handleChange = (elem) => {
    elem.preventDefault();
    setCountry(elem.target.value);
    // console.log(elem.target.value);
    // console.log(countryInfo);
  };

  let countryVal = `https://restcountries.eu/rest/v2/name/${country}`;

  if (country !== "") {
    axios
    .get(countryVal)
    .then((response) => {
      if (response.data.length > 5) {
        setMessage("Too much countries");
        console.log(response.data);
      } else {
        setCountryInfo(response.data);
        console.log(response.data);
      }
      // console.log(response.data);
    })
    .catch((error) => {
      // console.log(error);
    });
  }


  // if (countryInfo.length === 0) {
  //   setMessage("No country found / placed");
  //   return <p>{message}</p>;
  // } else if (countryInfo.length > 5) {
  //   setMessage("Too many countries");
  //   return <p>{message}</p>;
  // } else {
  //   console.log(countryInfo);
  // }

  return (
    <div className="container">
      <p>find countries</p>
      <input
        type="text"
        placeholder="search a country"
        onChange={handleChange}
      />

      <div>
        <p>{message}</p>
      </div>
    </div>
  );
}

ReactDOM.render(<App />, document.getElementById("root"));
