import React,  {useState, useEffect} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import axios from 'axios';

const App = () => {

  const [country, setCountry] = useState("");
  const [countryInfo, setCountryInfo] = useState([]);


  const handleChange = (elem) => {
    elem.preventDefault();
    setCountry(elem.target.value);
    console.log(elem.target.value);
    console.log(countryInfo);
  }

  let countryVal = `https://restcountries.eu/rest/v2/name/${country}`;

  axios.get(countryVal).then((response) => {
    setCountryInfo(response.data);
  }).catch((error) => {
    // console.log(error);
  });

  const renderInfo = () => {
    if(countryInfo.length === 0) {
      return <p>No country yet</p>;
    } else if (countryInfo.length > 5) {
      return <p>Too many countries</p>;
    }
  }

  return (
    <div className="container">
      <p>find countries</p>
      <input type="text" placeholder="search a country" onChange={handleChange} />

     <div>
      {renderInfo()}
     </div>
    </div>
  );
}

ReactDOM.render(<App />, document.getElementById("root"));
